#dict字典d = dict(name='刘某某',age='18',gender='男')print("姓名：{}".format(d['name']))#传包含有双值'子序列'的序列转换为字典#双值序列，序列只有两个值[1,2] ('a',3),'cd'#子序列:如果序列中的元素也是序列，那就称这个元素为子序列#[(1,2)(3,5)]:双值子序列d = dict ([('name','孙某某'),('age','18')])#双值子序列，特殊情况使用d['address'] = '辽宁省'print(d)#len()获取字典中键值对的个数print(len(d))n = 'name'd['name'] = 'sun悟空'print(d[n])print(d.get('na e','默认Z'))d = {'a':'1','b':'2','c':'3'}d2 = {'d':'4','e':'5','f':'6'}d.update(d2)print(d)result = d.popitem()print('result =',result)print(d)aa = d.pop('a','这是默认值')print(d)print(aa)##字典的遍历##keys  values  items#返回字典所有key#####d = {'name':'孙悟空','age':'18'}print(d.keys())print(d.values())print(d.items())for i in d.keys():    print(d[i])print(d['name'])for v in d.values():    print(v)for b,c in d.items():    print("{}={}".format(b,c))